import{o as e,c as r,a}from"./app.204bb5a0.js";const l='{"title":"0524","description":"","frontmatter":{},"headers":[{"level":2,"title":"工作","slug":"工作"},{"level":2,"title":"好文推荐","slug":"好文推荐"},{"level":2,"title":"项目推荐","slug":"项目推荐"},{"level":2,"title":"大佬推荐","slug":"大佬推荐"},{"level":2,"title":"好用的工具：","slug":"好用的工具："},{"level":2,"title":"反思","slug":"反思"},{"level":2,"title":"明日计划","slug":"明日计划"},{"level":2,"title":"TODOS","slug":"todos"}],"relativePath":"record/05/0524.md","lastUpdated":1631954527536}',t={},i=a('<h1 id="_0524"><a class="header-anchor" href="#_0524" aria-hidden="true">#</a> 0524</h1><h2 id="工作"><a class="header-anchor" href="#工作" aria-hidden="true">#</a> 工作</h2><ul><li>一个小需求开发：重新审核。</li><li>业务上，一个业务闭环正常来说就是最后一步审核，审核要么通过，不通过，然后就结束了。但是也存在类似超级管理员的身份，去重新处理一些审核错误的数据。</li><li>有意思的点： <ul><li>关于 使用 AntD 表格的时候，会需要设置 <code>width</code> 保证足够宽度。我之前错误的做法就是，调参，去试合适的数值使得所有的列都展示ok。实际上，页面是按照 UI 给定的最小宽度设计的。而最小宽度是 1280 的，左侧菜单栏以及内容的间距都是定值，因此，表格的内容宽度也要按照这个尺寸来调节，这样才能保证更小宽度的屏幕弹性布局的时候，不会换行显示弄得很丑。</li><li>关于换行，可能需要的方案是，使用 <code>span</code> 将内容包裹起来，然后设置 样式 <code>white-space: nowrap;</code>，这样就可以不换行啦。</li></ul></li></ul><h2 id="好文推荐"><a class="header-anchor" href="#好文推荐" aria-hidden="true">#</a> 好文推荐</h2><ul><li><a href="https://mp.weixin.qq.com/s/woTY95bMumnD82aUjTKLeg" target="_blank" rel="noopener noreferrer">精读《前端职业规划 - 2021 年》</a> 从 <strong>知识分类、领域深耕、经济视角</strong> 这几个视角谈谈前端职业规划</li><li><a href="https://juejin.cn/post/6906028995133833230" target="_blank" rel="noopener noreferrer">分享8个非常实用的Vue自定义指令</a> 如题，好用的指令。</li></ul><h2 id="项目推荐"><a class="header-anchor" href="#项目推荐" aria-hidden="true">#</a> 项目推荐</h2><h2 id="大佬推荐"><a class="header-anchor" href="#大佬推荐" aria-hidden="true">#</a> 大佬推荐</h2><ul><li><a href="https://juejin.cn/user/2330620381629070/posts" target="_blank" rel="noopener noreferrer">掘金/lzg9527</a> 优秀的大佬，输出关于 vue 的文章很多，如：<a href="https://juejin.cn/post/6844904152389124103" target="_blank" rel="noopener noreferrer">搭建一个vue-cli4+webpack移动端框架</a> 等</li></ul><h2 id="好用的工具："><a class="header-anchor" href="#好用的工具：" aria-hidden="true">#</a> 好用的工具：</h2><ul><li><a href="https://juejin.cn/post/6965382258341445646" target="_blank" rel="noopener noreferrer">Vscode开发工具怎么少得了这款插件（妹子开心了）</a> Vue VSCode Snippets插件安装和使用</li></ul><h2 id="反思"><a class="header-anchor" href="#反思" aria-hidden="true">#</a> 反思</h2><h2 id="明日计划"><a class="header-anchor" href="#明日计划" aria-hidden="true">#</a> 明日计划</h2><h2 id="todos"><a class="header-anchor" href="#todos" aria-hidden="true">#</a> TODOS</h2>',13);t.render=function(a,l,t,h,n,o){return e(),r("div",null,[i])};export default t;export{l as __pageData};
